package com.mycompany.veterinaria.controller;import com.mycompany.veterinaria.dto.MascotaDTO;import com.mycompany.veterinaria.model.Duenho;import com.mycompany.veterinaria.model.Mascota;import com.mycompany.veterinaria.service.IMascotaService;import com.mycompany.veterinaria.service.MascotaService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/api/mascota")public class MascotaController {    @Autowired    private IMascotaService mascotaService;    @GetMapping("/get")    public List<Mascota> getMascotas (){        return  mascotaService.getMascotas();    }    @DeleteMapping("/delete")    public String deleteMascota (@RequestParam Long idMascota){        mascotaService.deleteMascota(idMascota);        return "Mascota eliminado";    }    @GetMapping("/get/one")    public Mascota findDuenho (@RequestParam Long idMascota){        Mascota mascota = mascotaService.findOneMascota(idMascota);        return mascota;    }    @PutMapping("/edit")    public Mascota editMascota (@RequestBody Mascota mascota){        mascotaService.editMascota(mascota);        return  mascotaService.findOneMascota(mascota.getIdMascota());    }    @PostMapping("/save")    public String saveDuenho (@RequestBody Mascota mascota){        mascotaService.saveMascota(mascota);        return  "Mascota creado";    }    @GetMapping ("/get/perro")    public List<Mascota> getPerro (){        return  mascotaService.getPerro();    }    @GetMapping ("/get/mascotasDTO")    public  List<MascotaDTO> findMascotaDTO (){        return mascotaService.findMascotaDTO();    }}