package com.mycompany.veterinaria.dto;import lombok.Getter;import lombok.Setter;@Getter @Setterpublic class PetDTO {    private String petName;    private String species ;    private String race;    private String ownerName;    private  String ownerLastname;    public PetDTO() {    }    public PetDTO(String petName, String species, String race, String ownerName, String ownerLastname) {        this.petName = petName;        this.species = species;        this.race = race;        this.ownerName = ownerName;        this.ownerLastname = ownerLastname;    }}