package com.mycompany.veterinaria.model;import com.fasterxml.jackson.annotation.JsonBackReference;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;@Getter @Setter@Entitypublic class Mascota {    @Id    @GeneratedValue (strategy = GenerationType.SEQUENCE)    @Column(name = "id_mascota")    private  Long idMascota ;    private String nombreMascota;    private  String especie;    private String raza;    private String color ;    @ManyToOne    @JoinColumn(name = "duenho_id")    @JsonBackReference    private Duenho duenho;    public Mascota() {    }    public Mascota(Long idMascota, String nombreMascota, String especie, String raza, String color, Duenho duenho) {        this.idMascota = idMascota;        this.nombreMascota = nombreMascota;        this.especie = especie;        this.raza = raza;        this.color = color;        this.duenho = duenho;    }}