package com.mycompany.veterinaria.model;import com.fasterxml.jackson.annotation.JsonManagedReference;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;import java.util.List;@Getter @Setter@Entitypublic class Owner {    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE)    @Column(name = "id_owner")    private  Long idOwner;    private String dni ;    private String ownerName ;    private String ownerLastname;    private String ownerCellphone;    @OneToMany(mappedBy = "owner", cascade = CascadeType.ALL, orphanRemoval = true)    @JsonManagedReference    private List<Pet> pets;    public Owner() {    }    public Owner(Long idOwner, String dni, String ownerName, String ownerLastname, String ownerCellphone, List<Pet> pets) {        this.idOwner = idOwner;        this.dni = dni;        this.ownerName = ownerName;        this.ownerLastname = ownerLastname;        this.ownerCellphone = ownerCellphone;        this.pets = pets;    }}