package com.mycompany.veterinaria.model;import com.fasterxml.jackson.annotation.JsonBackReference;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;@Getter @Setter@Entitypublic class Pet {    @Id    @GeneratedValue (strategy = GenerationType.SEQUENCE)    @Column(name = "id_pet")    private  Long idPet ;    private String petName;    private  String species;    private String race;    private String color ;    @ManyToOne    @JoinColumn(name = "owner_id")    @JsonBackReference    private Owner owner;    public Pet() {    }    public Pet(Long idPet, String petName, String species, String race, String color, Owner owner) {        this.idPet = idPet;        this.petName = petName;        this.species = species;        this.race = race;        this.color = color;        this.owner = owner;    }}